export default function (args = process.argv.slice(2), separator) {
    const mainArgs = [];
    const trailingArgs = [];
    let separatorFound = false;
    args.forEach((arg) => {
        if (arg === separator) {
            return (separatorFound = true);
        }
        if (separatorFound) {
            trailingArgs.push(arg);
        }
        else {
            mainArgs.push(arg);
        }
    });
    return [mainArgs, trailingArgs];
}
